import logo from './logo.svg';
import './App.css';
import { useEffect, useState } from 'react';
import Card from './components/card';
import axios from 'axios';
import backgroundImage  from './peakpx.jpg'


function App() {


  const API_KEY = '10f5fc043a0815d3b2db734dd151a90a';
  const url = "https://api.openweathermap.org/data/2.5/weather?q=${Buenos Aires}&appid=${API_KEY}&units=metric"
  const [cityarg, setCityArg] = useState('Buenos Aires')
  const [city, setCity] = useState(cityarg);
  const [data, setData] = useState()
  const [loading, setLoading] = useState()


  // useEffect(() => {
  //   axios.get(url)
  // }, [] )


  const fetchData = async () => {
    await axios
    .get(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`)
    .then((res, req) => {
      console.log(res)
      setData(res)
      setLoading(false)
    })
    .catch((error) => {
      console.log(error)
    })
  }



  return (
    <div className="App" style={{ backgroundImage: `url(${backgroundImage})`, backgroundSize: "cover" }}>
      <Card data={data} setCity={setCity}/>
    </div>
  );
}

export default App;
